    }

    a:hover,
    a:focus,
    a:active {
        color: var(--clr-primary-400);
    }

    @include mq(40em) {
        position: static;
        background: transparent;

        ul > * + * {
            margin-left: 3em;
        }

        a:link,
        a:visited {
            color: currentColor;
            transition: all 350ms ease-in-out;
        }

        a:hover,
        a:focus,
        a:active {
            transform: scale(1.3);
            opacity: 0.7;
        }
    }
}

@include mq(40em) {
    .header__inner {
        display: flex;
        align-items: center;
        justify-content: space-between;
    }
}

.header .container {
    width: 100%;
    margin-top: auto;
    margin-bottom: auto;

    & > * {
        max-width: 12ch;
    }
}

.navigation-secondary {
    display: flex;
    flex-wrap: wrap;
    align-items: center;
    margin: 2rem 0;

    &__list {
        display: flex;
        flex-wrap: wrap;
        gap: 1rem;
        margin: 0;
    }

    > .btn {
        margin-left: auto;
    }
}

