@use '../tools' as *;

.footer {
    padding-bottom: 3rem;

    &__inner {
        @include section-border;
    }

    dt {
        font-family: var(--ff-serif);
        font-weight: var(--fw-200);
        color: var(--clr-neutral-400);
    }

    dd > a {
        font-weight: var(--fw-700);
        font-size: var(--fs-300);

        &:hover,
        &:focus {
            color: var(--clr-primary-400);
        }
    }

    @include mq(40em) {
        padding-bottom: 0;
    }
}

.copyright {
    &::before {
        content: "\00A9";
    }
}

.social {
    &__list {
        display: flex;
        justify-content: center;
    }
}
